import React, { useState } from 'react'

function Game() {
    
    let [square, setSquare] = useState([null, null, null, null, null, null, null, null, null]);
    let [isX, setIsX] = useState(true);
    let handleClick = (event) => {
        let string = event.target.id;
        let numb = Number(string.slice(-1));
        square[numb] = isX ? "X" : "O";
        setIsX(!isX);
     }
  return (
    <div className='game-box'>
      <div>
      <button className='square' id="box0" onClick={handleClick}>{square[0]}</button>
      <button className='square' id="box1" onClick={handleClick}>{square[1]}</button>
      <button className='square' id="box2" onClick={handleClick}>{square[2]}</button>
      </div>
      <div>
      <button className='square' id="box3" onClick={handleClick}>{square[3]}</button>
      <button className='square' id="box4" onClick={handleClick}>{square[4]}</button>
      <button className='square' id="box5" onClick={handleClick}>{square[5]}</button>
      </div>
      <div>
      <button className='square' id="box6" onClick={handleClick}>{square[6]}</button>
      <button className='square' id="box7" onClick={handleClick}>{square[7]}</button>
      <button className='square' id="box8" onClick={handleClick}>{square[8]}</button>
      </div>
    </div>
  )
}

export default Game
